    public function getSourceContext()
    {
        return new Source("{% extends \"base.html.twig\" %}

{% block title %}Création d'une annonce {% endblock %}

{% form_theme form 'ad/_collection.html.twig' %} {# form_theme permet de rendre au courant twig que nous voulons utiliser un theme particulier #}


{% block body %}
    <div class=\"container\">
        <h1 class=\"my-5\">Créer une nouvelle annonce</h1>

        {{ form_start(form)}}
        <div class=\"row\">
            <div class=\"col\">
                <div class=\"alert alert-light\">
                    <h2>Informations générales</h2>
                    <hr>
                    {{form_row(form.title)}}
                    {{form_row(form.slug)}}
                    {{form_row(form.price)}}
                    {{form_row(form.rooms)}}
                </div>

            </div>
            <div class=\"col\">
                <div class=\"alert alert-light\">
                    <h2>Image de l'annonce</h2>
                    <hr>
                    {{form_row(form.coverImage)}}
                    {{form_row(form.images)}}
                </div>
            </div>
        </div>
        <div class=\"row\">
            <div class=\"col-12\">
                <div class=\"alert alert-light\">
                    <h2>Détails de l'annonce</h2>
                    <hr>
                    {{form_row(form.introduction)}}
                    {{form_row(form.content)}}
                </div>
            </div>
        </div>
        <div class=\"alert alert-success clearfix\">
            <h2 class=\"alert-heading\">Sauvegarder mon annonce</h2>
            <p>Vous êtes sur le point de créer votre annonce, nous sommes fière de vous ! </p>
            <button type=\"submit\" class=\"btn btn-primary float-right\"><i class=\"fas fa-check\"></i> Créer une nouvelle annonce</button>
            
        </div>


        {{form_end(form)}}
    </div>
{% endblock %}

{% block javascripts %}
<script src=\"/js/ad.js\"></script>
{% endblock %}

