    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block body %}
<div class=\"container\">
    <div class=\"row\">
        <div class=\"col-md-3\">
        
        </div>
        <div class=\"col-md-6\">
            <div class=\"bg-light py-3 px-3\">
                <h1>Connectez-vous :</h1>
                <form method=\"post\">
                    {% if error %}
                        <div class=\"alert alert-danger\">Vos identifiants sont incorrects</div>
                    {% endif %}

                    {% if app.user %}
                        <div class=\"mb-3\">
                            Vous êtes connecté en tant que <strong>{{ app.user.username }},</strong> <a href=\"{{ path('app_logout') }}\">Se déconnecter</a>
                        </div>
                    {% endif %}
                    <label for=\"inputEmail\">Email</label>
                    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
                    <label for=\"inputPassword\">Password</label>
                    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>

                    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\"><br>

                    {#
                        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
                        See https://symfony.com/doc/current/security/remember_me.html

                        <div class=\"checkbox mb-3\">
                            <label>
                                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
                            </label>
                        </div>
                    #}

                    <button class=\"btn btn-lg btn-primary\" type=\"submit\">Connexion</button>
                </form>
            </div>
        </div>
    </div>
</div>
{% endblock %}
", "security/login.html.twig", "/Applications/MAMP/htdocs/lineup/templates/security/login.html.twig");
